package com.concert.user.controller;

import com.concert.user.model.User;
import com.concert.user.service.UserService;
import com.concert.user.dto.UserRegistrationDto;
import com.concert.user.dto.UserResponseDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import javax.validation.Valid;
import java.security.Principal;

@RestController
@RequestMapping("/api/users")
public class UserController {

    @Autowired
    private UserService userService;

    @PostMapping("/register")
    public ResponseEntity<UserResponseDto> registerUser(@Valid @RequestBody UserRegistrationDto registrationDto) {
        User user = userService.registerUser(registrationDto);
        return ResponseEntity.ok(userService.convertToDto(user));
    }

    @GetMapping("/me")
    public ResponseEntity<UserResponseDto> getCurrentUser(Principal principal) {
        User user = userService.getUserByUsername(principal.getName());
        return ResponseEntity.ok(userService.convertToDto(user));
    }

    @GetMapping("/{id}")
    public ResponseEntity<UserResponseDto> getUserById(@PathVariable String id) {
        User user = userService.getUserById(id);
        return ResponseEntity.ok(userService.convertToDto(user));
    }

    @PutMapping("/me")
    public ResponseEntity<UserResponseDto> updateUser(
            Principal principal,
            @Valid @RequestBody UserRegistrationDto userDto) {
        User updatedUser = userService.updateUser(principal.getName(), userDto);
        return ResponseEntity.ok(userService.convertToDto(updatedUser));
    }
}
