package com.concert.recommendation.service;

import com.concert.recommendation.client.UserServiceClient;
import com.concert.recommendation.client.ConcertServiceClient;
import com.concert.recommendation.model.Recommendation;
import com.concert.recommendation.repository.RecommendationRepository;
import com.concert.recommendation.dto.RecommendationDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.time.Instant;
import java.util.List;
import java.util.stream.Collectors;

@Service
public class RecommendationService {

    @Autowired
    private RecommendationRepository recommendationRepository;

    @Autowired
    private UserServiceClient userServiceClient;

    @Autowired
    private ConcertServiceClient concertServiceClient;

    public List<RecommendationDto> getRecommendationsForUser(String userId) {
        // Get user preferences from user service
        List<String> preferences = userServiceClient.getUserPreferences(userId);

        // Get user activity data
        List<String> attendedConcerts = concertServiceClient.getAttendedConcerts(userId);

        // Generate recommendations using collaborative filtering
        List<String> recommendedConcertIds = generateRecommendations(userId, preferences, attendedConcerts);

        // Save recommendations
        Recommendation recommendation = new Recommendation();
        recommendation.setUserId(userId);
        recommendation.setConcertIds(recommendedConcertIds);
        recommendation.setCreatedAt(Instant.now().toString());
        recommendationRepository.save(recommendation);

        // Return detailed concert information
        return recommendedConcertIds.stream()
            .map(concertId -> concertServiceClient.getConcertDetails(concertId))
            .collect(Collectors.toList());
    }

    private List<String> generateRecommendations(String userId, List<String> preferences, List<String> attendedConcerts) {
        // In a real system, this would implement a sophisticated recommendation algorithm
        // For simplicity, we'll just return some concert IDs
        return concertServiceClient.getRecommendedConcerts(preferences, attendedConcerts);
    }
}
