```tsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getUser, updateUserProfile } from '../api/userService';
import { User } from '../types/User';
import { Button, Card, Avatar, TextField, Grid, Typography } from '@mui/material';

const UserProfile: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const [user, setUser] = useState<User | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedUser, setEditedUser] = useState<Partial<User>>({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await getUser(userId);
        setUser(userData);
        setEditedUser(userData);
      } catch (error) {
        console.error('Failed to fetch user:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [userId]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      if (user && editedUser) {
        const updatedUser = await updateUserProfile(user.id, editedUser);
        setUser(updatedUser);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Failed to update profile:', error);
    }
  };

  const handleCancel = () => {
    setEditedUser(user || {});
    setIsEditing(false);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setEditedUser(prev => ({ ...prev, [name]: value }));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <div>User not found</div>;
  }

  return (
    <Card className="user-profile-card">
      <Grid container spacing={3}>
        <Grid item xs={12} md={4} className="profile-avatar-section">
          <Avatar
            src={user.profileImage || '/default-avatar.png'}
            alt={user.username}
            sx={{ width: 150, height: 150 }}
          />
          {!isEditing && (
            <Button variant="contained" color="primary" onClick={handleEdit}>
              Edit Profile
            </Button>
          )}
        </Grid>
        <Grid item xs={12} md={8}>
          {isEditing ? (
            <form>
              <TextField
                label="Username"
                name="username"
                value={editedUser.username || ''}
                onChange={handleChange}
                fullWidth
                margin="normal"
              />
              <TextField
                label="Bio"
                name="bio"
                value={editedUser.bio || ''}
                onChange={handleChange}
                fullWidth
                margin="normal"
                multiline
                rows={4}
              />
              <div className="button-group">
                <Button variant="contained" color="primary" onClick={handleSave}>
                  Save
                </Button>
                <Button variant="outlined" onClick={handleCancel}>
                  Cancel
                </Button>
              </div>
            </form>
          ) : (
            <>
              <Typography variant="h4">{user.username}</Typography>
              <Typography variant="body1" className="user-role">
                {user.role}
              </Typography>
              <Typography variant="body2" className="user-bio">
                {user.bio || 'No bio provided.'}
              </Typography>
              <div className="user-stats">
                <div>
                  <Typography variant="h6">Concerts Attended</Typography>
                  <Typography variant="body2">{user.concertsAttended || 0}</Typography>
                </div>
                <div>
                  <Typography variant="h6">Artists Following</Typography>
                  <Typography variant="body2">{user.followingCount || 0}</Typography>
                </div>
              </div>
            </>
          )}
        </Grid>
      </Grid>
    </Card>
  );
};

export default UserProfile;
```

