package com.concert.arrangement.controller;

import com.concert.arrangement.model.Arrangement;
import com.concert.arrangement.service.ArrangementService;
import com.concert.arrangement.dto.ArrangementRequestDto;
import com.concert.arrangement.dto.ArrangementResponseDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import javax.validation.Valid;
import java.security.Principal;
import java.util.List;

@RestController
@RequestMapping("/api/arrangements")
public class ArrangementController {

    @Autowired
    private ArrangementService arrangementService;

    @PostMapping
    public ResponseEntity<ArrangementResponseDto> createArrangement(
            Principal principal,
            @Valid @RequestBody ArrangementRequestDto requestDto) {
        Arrangement arrangement = arrangementService.createArrangement(principal.getName(), requestDto);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }

    @GetMapping("/{id}")
    public ResponseEntity<ArrangementResponseDto> getArrangement(@PathVariable String id) {
        Arrangement arrangement = arrangementService.getArrangementById(id);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }

    @GetMapping("/artist")
    public ResponseEntity<List<ArrangementResponseDto>> getArtistArrangements(Principal principal) {
        List<Arrangement> arrangements = arrangementService.getArrangementsByArtist(principal.getName());
        return ResponseEntity.ok(
            arrangements.stream()
                .map(arrangementService::convertToDto)
                .collect(Collectors.toList())
        );
    }

    @PutMapping("/{id}/venue")
    public ResponseEntity<ArrangementResponseDto> selectVenue(
            @PathVariable String id,
            @RequestBody String venueId) {
        Arrangement arrangement = arrangementService.selectVenue(id, venueId);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }

    @PutMapping("/{id}/sessions")
    public ResponseEntity<ArrangementResponseDto> addSessions(
            @PathVariable String id,
            @RequestBody List<String> sessionIds) {
        Arrangement arrangement = arrangementService.addSessions(id, sessionIds);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }

    @PutMapping("/{id}/merchandise")
    public ResponseEntity<ArrangementResponseDto> addMerchandise(
            @PathVariable String id,
            @RequestBody List<String> merchandiseIds) {
        Arrangement arrangement = arrangementService.addMerchandise(id, merchandiseIds);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }

    @PostMapping("/{id}/finalize")
    public ResponseEntity<ArrangementResponseDto> finalizeArrangement(@PathVariable String id) {
        Arrangement arrangement = arrangementService.finalizeArrangement(id);
        return ResponseEntity.ok(arrangementService.convertToDto(arrangement));
    }
}
