name: Deploy to AWS EKS

on:
  push:
    branches: [ main ]

env:
  AWS_REGION: us-west-2
  ECR_REPOSITORY_PREFIX: concert-app

jobs:
  build-and-deploy:
    name: Build and Deploy
    runs-on: ubuntu-latest

    steps:
    - name: Checkout
      uses: actions/checkout@v2

    - name: Configure AWS credentials
      uses: aws-actions/configure-aws-credentials@v1
      with:
        aws-access-key-id: ${{ secrets.AWS_ACCESS_KEY_ID }}
        aws-secret-access-key: ${{ secrets.AWS_SECRET_ACCESS_KEY }}
        aws-region: ${{ env.AWS_REGION }}

    - name: Login to Amazon ECR
      id: login-ecr
      uses: aws-actions/amazon-ecr-login@v1

    - name: Build, tag, and push backend images to Amazon ECR
      env:
        ECR_REGISTRY: ${{ steps.login-ecr.outputs.registry }}
      run: |
        # Build and push user-service
        docker build -t $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:$GITHUB_SHA ./backend/user-service
        docker push $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:$GITHUB_SHA
        docker tag $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:$GITHUB_SHA $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:latest
        docker push $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:latest

        # Repeat for other services
        # ...

    - name: Build, tag, and push frontend images to Amazon ECR
      env:
        ECR_REGISTRY: ${{ steps.login-ecr.outputs.registry }}
      run: |
        # Build and push frontend
        docker build -t $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:$GITHUB_SHA ./frontend
        docker push $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:$GITHUB_SHA
        docker tag $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:$GITHUB_SHA $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:latest
        docker push $ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:latest

    - name: Update kube config
      run: aws eks update-kubeconfig --name concert-app-cluster --region ${{ env.AWS_REGION }}

    - name: Deploy to EKS
      run: |
        # Update kubernetes deployment images
        kubectl set image deployment/user-service user-service=$ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-user-service:$GITHUB_SHA -n concert-app
        # Repeat for other services
        # ...
        kubectl set image deployment/frontend frontend=$ECR_REGISTRY/$ECR_REPOSITORY_PREFIX-frontend:$GITHUB_SHA -n concert-app
