package com.concert.ticket.controller;

import com.concert.ticket.model.Ticket;
import com.concert.ticket.service.TicketService;
import com.concert.ticket.dto.TicketPurchaseRequest;
import com.concert.ticket.dto.TicketResponseDto;
import com.concert.ticket.dto.TicketInventoryDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import javax.validation.Valid;
import java.security.Principal;
import java.util.List;
import java.util.stream.Collectors;

@RestController
@RequestMapping("/api/tickets")
public class TicketController {

    @Autowired
    private TicketService ticketService;

    @GetMapping("/events/{eventId}")
    public ResponseEntity<TicketInventoryDto> getTicketsForEvent(@PathVariable String eventId) {
        TicketInventoryDto inventory = ticketService.getTicketInventoryForEvent(eventId);
        return ResponseEntity.ok(inventory);
    }

    @PostMapping("/reserve")
    public ResponseEntity<TicketResponseDto> reserveTicket(
            @Valid @RequestBody TicketPurchaseRequest request,
            Principal principal) {
        Ticket ticket = ticketService.reserveTicket(request, principal.getName());
        return ResponseEntity.ok(ticketService.convertToDto(ticket));
    }

    @PostMapping("/purchase")
    public ResponseEntity<TicketResponseDto> purchaseTicket(
            @Valid @RequestBody TicketPurchaseRequest request,
            Principal principal) {
        Ticket ticket = ticketService.purchaseTicket(request, principal.getName());
        return ResponseEntity.ok(ticketService.convertToDto(ticket));
    }

    @GetMapping("/user")
    public ResponseEntity<List<TicketResponseDto>> getUserTickets(Principal principal) {
        List<Ticket> tickets = ticketService.getTicketsByUser(principal.getName());
        List<TicketResponseDto> dtos = tickets.stream()
            .map(ticketService::convertToDto)
            .collect(Collectors.toList());
        return ResponseEntity.ok(dtos);
    }

    @GetMapping("/{id}")
    public ResponseEntity<TicketResponseDto> getTicket(@PathVariable String id) {
        Ticket ticket = ticketService.getTicketById(id);
        return ResponseEntity.ok(ticketService.convertToDto(ticket));
    }

    @PostMapping("/{id}/validate")
    public ResponseEntity<Boolean> validateTicket(
            @PathVariable String id,
            @RequestBody String validationCode) {
        boolean isValid = ticketService.validateTicket(id, validationCode);
        return ResponseEntity.ok(isValid);
    }

    @PostMapping("/issue")
    public ResponseEntity<List<TicketResponseDto>> issueTickets(
            @Valid @RequestBody TicketInventoryDto inventoryRequest) {
        List<Ticket> tickets = ticketService.issueTickets(inventoryRequest);
        List<TicketResponseDto> dtos = tickets.stream()
            .map(ticketService::convertToDto)
            .collect(Collectors.toList());
        return ResponseEntity.ok(dtos);
    }
}
