### ticket-service/src/main/java/com/concert/ticket/service/TicketService.java
```java
package com.concert.ticket.service;

import com.concert.ticket.model.Ticket;
import com.concert.ticket.model.TicketStatus;
import com.concert.ticket.repository.TicketRepository;
import com.concert.ticket.dto.TicketPurchaseRequest;
import com.concert.ticket.dto.TicketResponseDto;
import com.concert.ticket.dto.TicketInventoryDto;
import com.concert.ticket.exception.ResourceNotFoundException;
import com.concert.ticket.exception.TicketNotAvailableException;
import com.concert.ticket.client.PaymentServiceClient;
import com.concert.ticket.client.EventServiceClient;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import java.math.BigDecimal;
import java.time.Instant;
import java.util.List;
import java.util.UUID;
import java.util.stream.Collectors;

@Service
public class TicketService {

    @Autowired
    private TicketRepository ticketRepository;

    @Autowired
    private PaymentServiceClient paymentServiceClient;

    @Autowired
    private EventServiceClient eventServiceClient;

    public TicketInventoryDto getTicketInventoryForEvent(String eventId) {
        List<Ticket> tickets = ticketRepository.findByEventId(eventId);

        TicketInventoryDto inventory = new TicketInventoryDto();
        inventory.setEventId(eventId);
        inventory.setTotalTickets(tickets.size());
        inventory.setAvailableTickets((int) tickets.stream()
            .filter(t -> t.getStatus() == TicketStatus.AVAILABLE)
            .count());
        return inventory;
    }

    @Transactional
    public Ticket reserveTicket(TicketPurchaseRequest request, String userId) {
        Ticket ticket = ticketRepository.findByEventIdAndSectionAndSeatAndStatus(
            request.getEventId(), request.getSection(), request.getSeat(), TicketStatus.AVAILABLE)
            .orElseThrow(() -> new TicketNotAvailableException("Ticket not available"));

        ticket.setStatus(TicketStatus.RESERVED);
        ticket.setUserId(userId);
        ticket.setUpdatedAt(Instant.now());

        return ticketRepository.save(ticket);
    }

    @Transactional
    public Ticket purchaseTicket(TicketPurchaseRequest request, String userId) {
        // First reserve the ticket
        Ticket ticket = reserveTicket(request, userId);

        // Process payment
        String paymentId = paymentServiceClient.processPayment(
            userId, ticket.getId(), ticket.getPrice(), "Ticket purchase");

        if (paymentId != null) {
            ticket.setStatus(TicketStatus.PURCHASED);
            ticket.setPurchaseId(paymentId);
            ticket.setValidationCode(generateValidationCode());
            ticket.setUpdatedAt(Instant.now());
            return ticketRepository.save(ticket);
        } else {
            // If payment fails, release the ticket
            ticket.setStatus(TicketStatus.AVAILABLE);
            ticket.setUserId(null);
            ticket.setUpdatedAt(Instant.now());
            ticketRepository.save(ticket);
            throw new RuntimeException("Payment failed");
        }
    }

    public List<Ticket> getTicketsByUser(String userId) {
        return ticketRepository.findByUserId(userId);
    }

    public Ticket getTicketById(String id) {
        return ticketRepository.findById(id)
            .orElseThrow(() -> new ResourceNotFoundException("Ticket not found"));
    }

    public boolean validateTicket(String id, String validationCode) {
        Ticket ticket = getTicketById(id);
        if (ticket.getStatus() != TicketStatus.PURCHASED) {
            return false;
        }

        if (ticket.getValidationCode().equals(validationCode)) {
            ticket.setStatus(TicketStatus.USED);
            ticket.setUpdatedAt(Instant.now());
            ticketRepository.save(ticket);
            return true;
        }

        return false;
    }

    @Transactional
    public List<Ticket> issueTickets(TicketInventoryDto inventoryRequest) {
        // Verify that the event exists
        eventServiceClient.verifyEventExists(inventoryRequest.getEventId());

        List<Ticket> tickets = inventoryRequest.getSections().stream()
            .flatMap(section -> {
                return section.getSeats().stream().map(seat -> {
                    Ticket ticket = new Ticket();
                    ticket.setEventId(inventoryRequest.getEventId());
                    ticket.setSection(section.getName());
                    ticket.setSeat(seat.getName());
                    ticket.setPrice(seat.getPrice());
                    ticket.setStatus(TicketStatus.AVAILABLE);
                    ticket.setCreatedAt(Instant.now());
                    ticket.setUpdatedAt(Instant.now());
                    return ticket;
                });
            })
            .collect(Collectors.toList());

        return ticketRepository.saveAll(tickets);
    }

    public TicketResponseDto convertToDto(Ticket ticket) {
        TicketResponseDto dto = new TicketResponseDto();
        dto.setId(ticket.getId());
        dto.setEventId(ticket.getEventId());
        dto.setSection(ticket.getSection());
        dto.setSeat(ticket.getSeat());
        dto.setPrice(ticket.getPrice());
        dto.setStatus(ticket.getStatus().toString());
        dto.setPurchaseDate(ticket.getUpdatedAt());
        return dto;
    }

    private String generateValidationCode() {
        return UUID.randomUUID().toString().substring(0, 8).toUpperCase();
    }
}
