import com.concert.artist.service.ArtistService;
import com.concert.artist.dto.ArtistDto;
import com.concert.artist.dto.ExperienceDto;
import com.concert.artist.dto.MediaDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.multipart.MultipartFile;

import javax.validation.Valid;
import java.security.Principal;
import java.util.List;
import java.util.stream.Collectors;

@RestController
@RequestMapping("/api/artists")
public class ArtistController {

    @Autowired
    private ArtistService artistService;

    @GetMapping
    public ResponseEntity<List<ArtistDto>> getAllArtists(
            @RequestParam(required = false) String genre,
            @RequestParam(required = false, defaultValue = "0") int page,
            @RequestParam(required = false, defaultValue = "10") int size) {
        List<Artist> artists = artistService.getAllArtists(genre, page, size);
        List<ArtistDto> artistDtos = artists.stream()
            .map(artistService::convertToDto)
            .collect(Collectors.toList());
        return ResponseEntity.ok(artistDtos);
    }

    @GetMapping("/{id}")
    public ResponseEntity<ArtistDto> getArtist(@PathVariable String id) {
        Artist artist = artistService.getArtistById(id);
        return ResponseEntity.ok(artistService.convertToDto(artist));
    }

    @PostMapping
    public ResponseEntity<ArtistDto> createArtist(
            @Valid @RequestBody ArtistDto artistDto,
            Principal principal) {
        Artist artist = artistService.createArtist(artistDto, principal.getName());
        return ResponseEntity.ok(artistService.convertToDto(artist));
    }

    @PutMapping("/{id}")
    public ResponseEntity<ArtistDto> updateArtist(
            @PathVariable String id,
            @Valid @RequestBody ArtistDto artistDto,
            Principal principal) {
        Artist artist = artistService.updateArtist(id, artistDto, principal.getName());
        return ResponseEntity.ok(artistService.convertToDto(artist));
    }

    @PostMapping("/{id}/experiences")
    public ResponseEntity<ExperienceDto> addExperience(
            @PathVariable String id,
            @Valid @RequestBody ExperienceDto experienceDto,
            Principal principal) {
        Experience experience = artistService.addExperience(id, experienceDto, principal.getName());
        return ResponseEntity.ok(artistService.convertToExperienceDto(experience));
    }

    @PostMapping("/{id}/media")
    public ResponseEntity<MediaDto> addMedia(
            @PathVariable String id,
            @RequestParam("file") MultipartFile file,
            @RequestParam("type") String type,
            @RequestParam("description") String description,
            Principal principal) {
        Media media = artistService.addMedia(id, file, type, description, principal.getName());
        return ResponseEntity.ok(artistService.convertToMediaDto(media));
    }

    @GetMapping("/user")
    public ResponseEntity<ArtistDto> getArtistByUser(Principal principal) {
        Artist artist = artistService.getArtistByUserId(principal.getName());
        return ResponseEntity.ok(artistService.convertToDto(artist));
    }

    @GetMapping("/popular")
    public ResponseEntity<List<ArtistDto>> getPopularArtists(
            @RequestParam(required = false, defaultValue = "10") int limit) {
        List<Artist> artists = artistService.getPopularArtists(limit);
        List<ArtistDto> artistDtos = artists.stream()
            .map(artistService::convertToDto)
            .collect(Collectors.toList());
        return ResponseEntity.ok(artistDtos);
    }
}
